import React, { useState, useEffect } from 'react'
// import { createBrowserHistory } from "history"
import { useHistory } from "react-router-dom";
import { Link } from 'react-router-dom'
import Contacts from './Contacts'
import '../App.css'
import { Form, Button } from 'react-bootstrap'
import 'bootstrap/dist/css/bootstrap.min.css'

const Login = () => {

    const history = useHistory()

    const [form, setForm] = useState({
        username: "",
        password: ""
    })

    const [users, setUsers] = useState({})

    const compareFetchToForm = (e) => {
        // debugger;
        // for(let obj of users.data){
        //     if (obj.username === form.username){
        //         history.push(`user/${obj.id}`)
        //     }
        //     else {
        //         alert("User does not exist!")
        //     }
        // }
        // debugger;
        let user = users.data.find((a) => a.username == form.username)
            history.push(`/users/${user.id}`)
    }

    const fetchUsers = () => {
        fetch("http://localhost:9292/users")
            .then(res => res.json())
            .then(data => setUsers(data))
            // .then(data => console.log(data))
    }
    useEffect(() => {
        fetchUsers()
    }, [])

    // const handleClick = (e) => {
    //     e.preventDefault()
    //     fetchForm(form)
    //     setForm({
    //         username: "",
    //         password: ""
    //     })
    //     e.target.reset()
    //     // alert("Account created head to Login link, and Login to continue!")
    // }

    const handleChange = (e) => {
        setForm({
            ...form,
            [e.target.name]: e.target.value,
        });
    };

    return (
        <div className = "sign-up-form">
            <Form>
                <Form.Group className="mb-3" controlId="formBasicEmail">
                    <Form.Label>Username</Form.Label>
                    <Form.Control type="text" placeholder="Enter Username" onChange={ handleChange } name = "username"/>
                </Form.Group>
                <Form.Group className="mb-3" controlId="formBasicPassword">
                    <Form.Label>Password</Form.Label>
                    <Form.Control type="password" placeholder="Enter Password" onChange={ handleChange } name = "password"/>
                </Form.Group>
                {/* <Link to={`user/${id}`}> */}
                    <Button variant="primary" type="submit" onClick={compareFetchToForm}>
                        Login
                    </Button>
                {/* </Link> */}
            </Form>
        </div>
    )
}

export default Login
